import React, { Component } from 'react'
import Task from './Task.js'


class MyTasks extends Component {

  state = {
    myTasks: this.props.myTasks
  }

  sortPriority = (task) => {
    debugger

    const updatePriority = { priority: action.task.counter + 1 }
      const prioritizedTask = { ...action.task.task, ...updatePriority }
      if (isInMyTaskState(action.task.task.attributes.id)) {
        const myTasks = state.myTasks.map(task => {
          return replaceIfEqual(task, prioritizedTask)
        })
        return { ...state, myTasks }
      } else {
        const assignedTasks = state.assignedTasks.map(task => {
          return replaceIfEqual(task, prioritizedTask)
        })
        return { ...state, assignedTasks }
      }
    return this.state.myTasks.sort(function (a, b) {
      const aPriority = a.priority ? a.priority : 0
      const bPriority = b.priority ? b.priority : 0
      return bPriority - aPriority
    })
  }


  render() {
    return (
      <div className="myTasks">
        <h6>{this.props.currentUser.attributes.name}'s Tasks:</h6>
        <ul>
          {this.props.myTasks && this.state.myTasks.map(task => {
            return (
              <div key={task.id}>
                <Task task={task} history={this.props.history} sortPriority={this.sortPriority} />
              </div>)
          })}
        </ul>
      </div>
    )
  }
}

export default MyTasks
