      //     {this.props.users.map(user => <option key={user.id} value={user.id}>{user.name}</option>)}
      //   </select>
      //   <br></br>
      //   <br></br>
      //   <Button variant="outline-secondary" type="submit">Add Task</Button>
      // </form>

      <Form onSubmit={this.handleSubmit}>
        <Form.Group controlId="comment">
          <Form.Control size="sm" type="text" name="content" value={this.state.content} placeholder="New comment.." onChange={this.handleChange} />
        </Form.Group>
        <Button size="sm" variant="outline-secondary" type="submit">Submit</Button>
      </Form>
    )
  }
}

const mapStateToProps = state => {
  return ({
    currentUser: state.currentUser,
    users: state.users
  })
}

export default connect(mapStateToProps, { postTask })(TaskForm)
