import React, { Component } from 'react'
import { login } from '../actions/currentUser'
import { connect } from 'react-redux'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'


class LoginForm extends Component {

  state = {
    email: "",
    password: ""
  }

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  handleSubmit = event => {
    event.preventDefault()
    this.props.login(this.state, this.props.history)
    this.setState({
      email: "",
      password: ""
    })
  }

  render() {
    return (
      // <form onSubmit={this.handleSubmit}>
      //   <label>Login:   </label>
      //   <br></br>
      //   <input placeholder="email" value={this.state.email} name="email" type="text" onChange={this.handleChange} />
      //   <input placeholder="password" value={this.state.password} name="password" type="text" onChange={this.handleChange} />
      //   <input type="submit" value="Log In" />
      // </form>
      <Form onSubmit={this.handleSubmit}>
        <Form.Group >
          <Form.Label>Login:</Form.Label>
          <Form.Control placeholder="email" value={this.state.email} name="email" type="text" onChange={this.handleChange} />
        </Form.Group>
        <Form.Group>
          <Form.Label>Login:</Form.Label>
          <Form.Control placeholder="email" value={this.state.email} name="email" type="text" onChange={this.handleChange} />
        </Form.Group>
        <Form.Group >
          <Form.Label>Who is this task assigned to:</Form.Label>
          <Form.Control as="select" name="user_id" id="user-select" onChange={this.handleChange} custom>
            <option value={this.props.currentUser.id}>Choose a user</option>
            {this.props.users.map(user => <option key={user.id} value={user.id}>{user.name}</option>)}
          </Form.Control>
        </Form.Group>
        <Button size="sm" variant="outline-secondary" type="submit">Submit</Button>
      </Form>
    )
  }
}

export default connect(null, { login })(LoginForm)
