import React, { Component } from 'react'
import LoginForm from './LoginForm.js'
import { login } from '../actions/currentUser'
import { signUp } from '../actions/currentUser'

import SignUpForm from './SignUpForm.js'
import { Switch, Route } from 'react-router-dom'
import { connect } from 'react-redux';


class AuthContainer extends Component {

  render() {
    return (
      <Switch>
        <Route
          path="/login"
          render={(routerProps) => <LoginForm {...routerProps} history={this.props.history} />}
        />
        <Route
          path="/signup"
          render={(routerProps) => <SignUpForm {...routerProps} departments={this.props.departments} />}
        />
      </Switch >
    )
  }
}

const mapStateToProps = state => {
  return ({
    departments: state.departments
  })
}

export default connect(mapStateToProps, { login, signup })(AuthContainer)
