import React from 'react'
import Task from './Task.js'


const MyTasks = props => {

  return (
    < div className="myTasks" >

      {console.log(props.myTasks)}

      <h6>{props.currentUser.attributes.name}'s Tasks:</h6>
      <ul>
        {props.myTasks && props.myTasks.map(task => {
          return (
            <div key={task.id}>
              <Task task={task} history={props.history} />
            </div>)
        })}
      </ul>
    </div >
  )
}

// const myTasks = sortByDate(tasks.filter(task => task.type === "task")
//   .filter(task => task.attributes.completed === false))

export default MyTasks
