export default (state = [], action) => {
  switch (action.type) {
    case "ADD_COMMENT":
      return state.concat(action.comment)
    case "DELETE_COMMENT":
      return state.filter(comment => comment.id === action.comment.id)
    default:
      return state
  }
}
